var length = 25;
console.log(25);

var hexString = length.toString(16);

if (hexString.length < 4) {
    while (hexString.length < 4) {
        hexString = '0' + hexString;
    }
}

console.log(hexString);

var num = parseInt(hexString, 16);
console.log(num);

