var type = '81';
var func = '0a';
var len = 12;

var buf = Buffer.alloc(4);
console.log(buf);

buf.write(type, 0, 'hex');
console.log(buf);

buf.write(func, 1, 'hex');
console.log(buf);

buf.writeInt16BE(len, 2);
console.log(buf);

